definition.script:
pipeline {
        agent any
        stages {
                stage('Build') {
                        steps{
                                build job: '1-Deploy_Linode_Ceph_Cluster', parameters: [password(description: 'Linode API Key', name: 'Linode_API_KEY', value: "$Linode_API_Key"),text(name: 'Linode_Cluster_Configuration', value: "$Linode_Cluster_Configuration"), string(name: 'ceph_iso_file', value: "$iso_file"), string(name: 'ceph_iso_path', value: "$iso_path"), text(name: 'ceph_ansible_all_config', value: "$all_config"), text(name: 'ceph_ansible_osds_config', value: "$osds_config")]
                        }
                }
                stage('Test Setup') {
                        steps{
                                build job: '2-Setup_Test_Tools', parameters: [booleanParam(name: 'sar', value: Boolean.valueOf(sar)), booleanParam(name: 'iostat', value: Boolean.valueOf(iostat)), booleanParam(name: 'mpstat', value: Boolean.valueOf(mpstat)), booleanParam(name: 'pidstat', value: Boolean.valueOf(pidstat)), booleanParam(name: 'proc-vmstat', value: Boolean.valueOf(proc_vmstat)), booleanParam(name: 'proc-interrupts', value: Boolean.valueOf(proc_interrupts)), booleanParam(name: 'turbostat', value: Boolean.valueOf(turbostat))]
                        }
                }
                stage('Test') {
                        steps{
                                build job: '3-CBT_Automated_Testing', parameters: [string(name: 'cbt_archive_dir', value: "$archive_dir"), text(name: 'cbt_settings', value: "$CBT_settings")]
                        }
                }
                stage('Analysis') {
                        steps{
                                build job: '4-Perform_Analysis', parameters: [string(name: 'Test_ID', value: "$Test_ID"), string(name: 'archive_dir', value: "$archive_dir")]
                        }
                }
        }
        post {
                always {
                        build job:'5-Teardown_Linode_Ceph_Cluster', parameters: [password(description: 'Linode API Key', name: 'Linode_API_Key', value: "$Linode_API_Key")]
                }
                success {
                    mail to: 'acalhoun@redhat.com',
                    subject: "Successful Pipeline: ${currentBuild.fullDisplayName}",
                    body: "Successful completion of ${Test_ID} \n Test results are posted on Elasticsearch/Grafana at the link below. \n http://10.18.81.12:3000/d/Dtsbj1iiz/rhcs-test-results?orgId=1&from=now%2Fd&to=now%2Fd&var-Test_ID=${Test_ID}"

                }
                failure {
                    mail to: 'acalhoun@redhat.com',
                    subject: "Failed Pipeline: ${currentBuild.fullDisplayName}",
                    body: "Something is wrong with ${env.BUILD_URL}"
                }

        }
}
