definition.script:
pipeline {
        agent {label 'master'}
        stages {
                stage('Build') {
                        steps{
                                build job: '1A-Retreive_RHCS_ISO_for_Linode', parameters: [string(name: 'ceph_iso_file', value: "$iso_file"), string(name: 'ceph_iso_path', value: "$iso_path"), string(name: 'agentname', value: "$node")]
                                build job: '1-Deploy_Linode_Ceph_Cluster', parameters: [password(description: 'Linode API Key', name: 'Linode_API_KEY', value: "$Linode_API_Key"),text(name: 'Linode_Cluster_Configuration', value: "$Linode_Cluster_Configuration"), text(name: 'ceph_ansible_all_config', value: "$all_config"), text(name: 'ceph_ansible_osds_config', value: "$osds_config"), [$class: 'NodeParameterValue', name: 'agentName', labels: ["$node"], , nodeEligibility: [$class: 'AllNodeEligibility']]]
                        }
                }
                stage('Test Setup') {
                        steps{
                                build job: '2-Setup_Test_Tools', parameters: [booleanParam(name: 'linode_cluster', value: true), booleanParam(name: 'sar', value: Boolean.valueOf(sar)), string(name: 'sar_interval', value: "$sar_interval"), booleanParam(name: 'iostat', value: Boolean.valueOf(iostat)), string(name: 'iostat_interval', value: "$iostat_interval"), booleanParam(name: 'mpstat', value: Boolean.valueOf(mpstat)), string(name: 'mpstat_interval', value: "$mpstat_interval"), booleanParam(name: 'pidstat', value: Boolean.valueOf(pidstat)), string(name: 'pidstat_interval', value: "$pidstat_interval"), booleanParam(name: 'proc-vmstat', value: Boolean.valueOf(proc_vmstat)), string(name: 'proc-vmstat_interval', value: "$proc_vmstat_interval"), booleanParam(name: 'proc-interrupts', value: Boolean.valueOf(proc_interrupts)), string(name: 'proc-interrupts_interval', value: "$proc_interrupts_interval"), booleanParam(name: 'turbostat', value: Boolean.valueOf(turbostat)), string(name: 'turbostat_interval', value: "$turbostat_interval"), [$class: 'NodeParameterValue', name: 'agentName', labels: ["$node"], , nodeEligibility: [$class: 'AllNodeEligibility']]]
                        }
                }
                stage('Test') {
                        steps{
                                build job: '3-CBT_Automated_Testing', parameters: [booleanParam(name: 'linode_cluster', value: true), string(name: 'cbt_archive_dir', value: "$archive_dir"), text(name: 'cbt_settings', value: "$CBT_settings"),  [$class: 'NodeParameterValue', name: 'agentName', labels: ["$node"], , nodeEligibility: [$class: 'AllNodeEligibility']]]
                        }
                }
                stage('Analysis') {
                        steps{
                                build job: '4-Perform_Analysis', parameters: [string(name: 'Test_ID', value: "$Test_ID"), string(name: 'archive_dir', value: "$archive_dir"), string(name: 'elasticsearch_host', value: 'localhost'), string(name: 'elasticsearch_port', value: '9200'), [$class: 'NodeParameterValue', name: 'agentName', labels: ["$node"], , nodeEligibility: [$class: 'AllNodeEligibility']], booleanParam(name: 'Evaluate_librbdfio_logs', value: true), booleanParam(name: 'Evaluate_pbench', value: true), booleanParam(name: 'Evaluate_librbdfio_json', value: true)]
                        }
                }
        }
        post {
                always {
                        build job:'5-Teardown_Linode_Ceph_Cluster', parameters: [password(description: 'Linode API Key', name: 'Linode_API_Key', value: "$Linode_API_Key"), [$class: 'NodeParameterValue', name: 'agentName', labels: ["$node"], , nodeEligibility: [$class: 'AllNodeEligibility']]]
                }
                success {
                    mail to: 'acalhoun@redhat.com',
                    subject: "Successful Pipeline: ${currentBuild.fullDisplayName}",
                    body: "Successful completion of ${Test_ID} \n Test results are posted on Elasticsearch/Grafana at the link below. \n http://10.18.81.12:2690/d/Dtsbj1iiz/rhcs-test-results?orgId=1&from=now%2Fd&to=now%2Fd&var-Test_ID=${Test_ID}"

                }
                failure {
                    mail to: 'acalhoun@redhat.com',
                    subject: "Failed Pipeline: ${currentBuild.fullDisplayName}",
                    body: "Something is wrong with ${env.BUILD_URL}"
                }

        }
}
